// Simulate realistic industrial sensor data
const baseTemp = 75;
const tempVariation = (Math.random() - 0.5) * 10;
const temperature = baseTemp + tempVariation;

const basePressure = 2.3;
const pressureVariation = (Math.random() - 0.5) * 0.5;
const pressure = basePressure + pressureVariation;

const baseFlow = 150;
const flowVariation = (Math.random() - 0.5) * 20;
const flowRate = baseFlow + flowVariation;

// Simulate occasional anomalies
const anomaly = Math.random() < 0.1; // 10% chance
if (anomaly) {
    temperature = temperature + (Math.random() * 20 - 10);
}

msg.payload = {
    timestamp: new Date().toISOString(),
    sensors: {
        temperature: Math.round(temperature * 10) / 10,
        pressure: Math.round(pressure * 100) / 100,
        flow_rate: Math.round(flowRate * 10) / 10,
        vibration: Math.round(Math.random() * 5 * 10) / 10,
        power_consumption: Math.round((500 + Math.random() * 100) * 10) / 10
    },
    status: {
        anomaly_detected: anomaly,
        system_health: anomaly ? 'warning' : 'normal',
        uptime_hours: Math.floor(Math.random() * 8760)
    },
    modbus_address: {
        temperature: 40001,
        pressure: 40002,
        flow_rate: 40003,
        vibration: 40004,
        power: 40005
    }
};

return msg;

const character = env.get('CHARACTER') || 'unknown';
const sensorData = msg.payload.sensors;
const status = msg.payload.status;

let characterResponse = {};

switch(character.toLowerCase()) {
    case 'kyoko':
        // Detective analysis - focus on anomalies
        const suspiciousReadings = [];
        if (sensorData.temperature > 85 || sensorData.temperature < 65) {
            suspiciousReadings.push('temperature');
        }
        if (sensorData.pressure > 3.0 || sensorData.pressure < 1.5) {
            suspiciousReadings.push('pressure');
        }
        if (sensorData.vibration > 3.5) {
            suspiciousReadings.push('vibration');
        }

        characterResponse = {
            character: 'ðŸ” Kyoko Kirigiri - Detective Analysis',
            analysis: 'Systematic investigation of sensor anomalies',
            findings: suspiciousReadings.length > 0 ? 
                `Suspicious readings detected in: ${suspiciousReadings.join(', ')}` : 
                'All readings within normal parameters',
            recommendation: suspiciousReadings.length > 0 ? 
                'Immediate investigation required' : 
                'Continue monitoring',
            confidence: suspiciousReadings.length > 0 ? '95%' : '90%'
        };
        break;

    case 'byakuya':
        // Efficiency analysis
        const efficiency = Math.round(((sensorData.flow_rate / 150) * 
                           (2.3 / sensorData.pressure) * 
                           (75 / sensorData.temperature)) * 100);

        characterResponse = {
            character: 'ðŸ’° Byakuya Togami - Efficiency Expert',
            analysis: 'Strategic performance optimization assessment',
            efficiency_rating: `${efficiency}%`,
            cost_impact: efficiency < 80 ? 'High operational cost detected' : 'Cost-optimal operation',
            recommendation: efficiency < 80 ? 
                'Implement immediate efficiency protocols' : 
                'Maintain current performance standards',
            roi_potential: efficiency < 80 ? '+15-25% improvement possible' : 'Optimal performance achieved'
        };
        break;

    case 'chihiro':
        // Technical data processing
        const dataQuality = {
            completeness: '100%',
            accuracy: status.anomaly_detected ? '85%' : '98%',
            latency: Math.round(Math.random() * 50) + 'ms'
        };

        characterResponse = {
            character: 'ðŸ’» Chihiro Fujisaki - Data Engineer',
            analysis: 'Advanced data processing and integration',
            data_quality: dataQuality,
            integration_status: 'All MODBUS registers successfully mapped',
            recommendation: 'Data pipeline operating optimally',
            next_optimization: 'Implementing predictive algorithms'
        };
        break;

    case 'celestia':
        // Risk assessment
        const riskFactors = [];
        let riskLevel = 'Low';

        if (status.anomaly_detected) riskFactors.push('Sensor anomaly');
        if (sensorData.vibration > 3.0) riskFactors.push('High vibration');
        if (sensorData.power_consumption > 580) riskFactors.push('Power spike');

        if (riskFactors.length > 2) riskLevel = 'High';
        else if (riskFactors.length > 0) riskLevel = 'Medium';

        characterResponse = {
            character: 'ðŸ‘‘ Celestia Ludenberg - Risk Analyst',
            analysis: 'Probability-based risk assessment',
            risk_level: riskLevel,
            risk_factors: riskFactors.length > 0 ? riskFactors : ['None detected'],
            probability: Math.round(riskFactors.length * 33.3) + '%',
            recommendation: riskLevel === 'High' ? 
                'Immediate risk mitigation required' : 
                'Continue standard monitoring'
        };
        break;

    case 'sakura':
        // Reliability and stress testing
        const systemStress = Math.round((
            (sensorData.temperature / 100) + 
            (sensorData.pressure / 4) + 
            (sensorData.vibration / 5) + 
            (sensorData.power_consumption / 600)
        ) * 25);

        characterResponse = {
            character: 'ðŸ’ª Sakura Ogami - Reliability Engineer',
            analysis: 'System endurance and reliability assessment',
            stress_level: `${systemStress}%`,
            uptime_target: '99.9%',
            current_uptime: `${status.uptime_hours} hours`,
            recommendation: systemStress > 75 ? 
                'System approaching stress limits - prepare maintenance' : 
                'System operating within tolerance',
            durability_rating: systemStress < 50 ? 'Excellent' : systemStress < 75 ? 'Good' : 'Concerning'
        };
        break;

    default:
        characterResponse = {
            character: 'â“ Standard Processing',
            analysis: 'Basic industrial monitoring',
            status: 'Data processed successfully'
        };
}

msg.payload.character_analysis = characterResponse;
return msg;

msg.payload = {
    alert_type: 'INDUSTRIAL_ANOMALY',
    severity: 'HIGH',
    character: msg.payload.character_analysis.character,
    timestamp: new Date().toISOString(),
    message: 'Anomaly detected in industrial system',
    sensor_data: msg.payload.sensors,
    character_analysis: msg.payload.character_analysis,
    action_required: 'Immediate investigation and response'
};

return msg;

msg.payload = {
    log_type: 'NORMAL_OPERATION',
    character: msg.payload.character_analysis.character,
    timestamp: new Date().toISOString(),
    sensors: msg.payload.sensors,
    analysis: msg.payload.character_analysis,
    status: 'System operating normally'
};

return msg;
