// Kyoko's Detective Analysis System
const timestamp = new Date();
const character = 'üîç Kyoko Kirigiri';

// Simulate industrial sensor data for investigation
const sensors = {
    temperature: 75 + (Math.random() - 0.5) * 20,
    pressure: 2.3 + (Math.random() - 0.5) * 0.8,
    vibration: Math.random() * 5,
    flow_rate: 150 + (Math.random() - 0.5) * 30,
    power: 500 + Math.random() * 100
};

// Detective analysis - look for anomalies
const anomalies = [];
let suspicionLevel = 0;

if (sensors.temperature > 85 || sensors.temperature < 65) {
    anomalies.push(`Temperature anomaly: ${sensors.temperature.toFixed(1)}¬∞C`);
    suspicionLevel += 30;
}

if (sensors.pressure > 3.0 || sensors.pressure < 1.8) {
    anomalies.push(`Pressure anomaly: ${sensors.pressure.toFixed(2)} bar`);
    suspicionLevel += 25;
}

if (sensors.vibration > 3.5) {
    anomalies.push(`High vibration detected: ${sensors.vibration.toFixed(1)}`);
    suspicionLevel += 20;
}

if (sensors.flow_rate < 120 || sensors.flow_rate > 180) {
    anomalies.push(`Flow rate irregular: ${sensors.flow_rate.toFixed(1)} L/min`);
    suspicionLevel += 15;
}

// Kyoko's investigation conclusion
let investigation_status;
let confidence;
let recommendation;

if (suspicionLevel > 50) {
    investigation_status = "üö® CRITICAL EVIDENCE FOUND";
    confidence = "95%";
    recommendation = "Immediate investigation required - multiple anomalies detected";
} else if (suspicionLevel > 25) {
    investigation_status = "‚ö†Ô∏è Suspicious Activity";
    confidence = "85%";
    recommendation = "Monitor closely - evidence suggests irregularities";
} else if (suspicionLevel > 0) {
    investigation_status = "üîç Minor Evidence";
    confidence = "70%";
    recommendation = "Continue observation - slight deviations noted";
} else {
    investigation_status = "‚úÖ No Evidence of Irregularities";
    confidence = "90%";
    recommendation = "Systems operating within normal parameters";
}

// Prepare dashboard data
const analysis = {
    character: character,
    timestamp: timestamp.toISOString(),
    investigation: {
        status: investigation_status,
        confidence: confidence,
        suspicion_level: suspicionLevel,
        evidence_count: anomalies.length,
        recommendation: recommendation
    },
    sensor_data: {
        temperature: Math.round(sensors.temperature * 10) / 10,
        pressure: Math.round(sensors.pressure * 100) / 100,
        vibration: Math.round(sensors.vibration * 10) / 10,
        flow_rate: Math.round(sensors.flow_rate * 10) / 10,
        power: Math.round(sensors.power * 10) / 10
    },
    evidence: anomalies,
    case_notes: "Detective analysis complete. All evidence catalogued and assessed."
};

// Send to different outputs
msg.payload = analysis;
msg.topic = "kyoko_investigation";

// For dashboard display
msg.ui_data = {
    status: investigation_status,
    confidence: confidence,
    evidence_count: anomalies.length,
    temperature: sensors.temperature.toFixed(1),
    pressure: sensors.pressure.toFixed(2),
    vibration: sensors.vibration.toFixed(1),
    suspicion: suspicionLevel
};

return msg;

// Format sensor data for chart display
const sensors = msg.payload.sensor_data;
const timestamp = new Date();

// Create multiple messages for different sensor readings
const messages = [];

// Temperature data
messages.push({
    payload: sensors.temperature,
    topic: "Temperature",
    timestamp: timestamp
});

// Pressure data
messages.push({
    payload: sensors.pressure * 10, // Scale for better visibility
    topic: "Pressure x10",
    timestamp: timestamp
});

// Vibration data
messages.push({
    payload: sensors.vibration * 15, // Scale for better visibility
    topic: "Vibration x15",
    timestamp: timestamp
});

return [messages];
